class hook179 extends _HOOK_CLASS_
{

/* !Hook Data - DO NOT REMOVE */
public static function hookData() {
 return array_merge_recursive( array (
  'mentionRow' => 
  array (
    0 => 
    array (
      'selector' => 'li.ipsMenu_item.ipsCursor_pointer > a > span.ipsPad_half[data-role=\'mentionname\']',
      'type' => 'replace',
      'content' => '<span class="ipsPad_half" data-role=\'mentionname\'>
  {$member->group[\'prefix\']|raw}{$member->name}{$member->group[\'suffix\']|raw}
</span>',
    ),
  ),
), parent::hookData() );
}
/* End Hook Data */


}

